let%expect_test "" =
  let str =
    {|
      type 'a list = 
        | Nil
        | Cons of 'a * 'a list
      ;;

      (* Non tail-recursive impl *)

      let rec append = 
        fun t1 t2 ->
          match t1 with
          ( Nil -> t2
          | Cons (x, t1) -> Cons (x, append t1 t2)
          )
      ;;

      let rec rev = 
        fun t ->
          match t with
          ( Nil -> Nil
          | Cons (x, t) -> append (rev t) (Cons (x, Nil)) 
          )
      ;;

      (* tail-recursive impl *)

      let rev = fun t -> 
        let rec loop = 
          fun t acc ->
            match t with
            ( Nil -> acc
            | Cons (x, t) -> loop t (Cons (x, acc))
            )
        in
        loop t Nil
      ;;
