let%expect_test "" =
  let str =
    {|
      type 'a list = 
        | Nil 
        | Cons of 'a * 'a list
      ;;

      external failwith : 'a. string -> 'a = "%failwith";;

      let rec take = 
        fun t n ->
          if n = 0 then Nil
          else 
            match t with
            ( Nil -> failwith "Invalid argument"
            | Cons (x, t) -> Cons (x, take t (n - 1))
            )
      ;;

      let rec drop = 
        fun t n ->
          if n = 0 then t 
          else
            match t with
            ( Nil -> failwith "Invalid argument"
            | Cons (_, t) -> drop t (n - 1)
            )
      ;;
    |}
  in
  print_infer_result str;
