let%expect_test "" =
  let str =
    {|
      type 'a tree = 
        | Br of 'a tree * 'a * 'a tree
        | Lf
      ;;

      let rec size = fun t -> 
        match t with
        ( Br (l, _, r) -> size l + 1 + size r
        | Lf -> 0
        )
      ;;

      let rec sum = 
        fun t ->
          match t with
          ( Br (l, x, r) -> x + sum l + sum r
          | Lf -> 0
          )
      ;;

      external lt : 'a. 'a -> 'a -> bool = "%less_than";;

      let max = fun x y ->
        if lt x y then y else x
      ;;

      let rec depth =
        fun t ->
          match t with
          ( Br (l, _, r) -> 1 + max (depth l) (depth r)
          | Lf -> 0
          )
      ;;

      let rec map = 
        fun t f ->
          match t with
          ( Br (l, x, r) -> Br (map l f, f x, map r f)
          | Lf -> Lf
          )
      ;;
    |}
