let%expect_test "" =
  let str =
    {|
      external print_int : int -> unit = "%print_int";;
      external print_newline : unit -> unit = "%print_newline";;

      let () = print_int 100;;

      let print_map_entry = 
        fun print (k, d) -> 
          print_int k; 
          print_newline (); 
          print d;
          print_newline ()
      ;;

      type 'a list =
        | Nil
        | Cons of 'a * 'a list
      ;;

      let rec print_map = 
        fun print t ->
          match t with
          ( Nil -> ()
          | Cons (x, t) ->
            print_map_entry print x;
            print_map print t
          )
      ;;

      let rec iter = 
        fun f t ->
          match t with
          ( Nil -> ()
          | Cons (x, t) -> f x; iter f t
          )
      ;;

      let print_map = 
        fun print -> iter (print_map_entry print)
      ;;
    |}
